var JSONList = Workout4bJSON;

var Output = {
    Templates: {
        1: {},
        2: {},
        3: {},
        4: {},
        5: {}
    },
    getWeekDay: {},
    getID: {}
};

var TemplateNum = 0;
for (var W = 1; W <= 4; W++) {
    for (var D = 1; D <= 4; D++) {
        TemplateNum++;
        if (!(W in Output.getID)) {
            Output.getID[W] = {};
            Output.getID[W][D] = TemplateNum;
        } else {
            Output.getID[W][D] = TemplateNum;
        }
        Output.Templates[W][D] = {
            ID: TemplateNum,
            Week: W,
            Day: D,
            NPatterns: 0,
            Patterns: {}
        };
        Output.getWeekDay[TemplateNum] = { Week: W, Day: D };
    }
}
for (var D = 1; D <= 3; D++) {
    TemplateNum++;
    if (!(5 in Output.getID)) {
        Output.getID[5] = {};
        Output.getID[5][D] = TemplateNum;
    } else {
        Output.getID[5][D] = TemplateNum;
    }
    Output.Templates[5][D] = {
        ID: TemplateNum,
        Week: 5,
        Day: D,
        NPatterns: 0,
        Patterns: {}
    };
    Output.getWeekDay[TemplateNum] = { Week: 5, Day: D };
}

module.exports = Output;

